mapper.select:
SELECT a.*, m.nickname AS extra__writerName
FROM `reply` AS a
LEFT JOIN `member` AS m
ON a.memberId = m.id
WHERE  a.relTypeCode = #{relTypeCode} 
AND a.relId = #{id};

SELECT *
FROM `reply`
WHERE id = #{id} 
AND relTypeCode = "article" ;

mapper.update:
UPDATE `reply`
SET updateDate = NOW(),
`body` = #{body}
WHERE id = #{id}

UPDATE `reply`
SET goodReactionPoint = goodReactionPoint+1 
WHERE id = #{id};

UPDATE `reply`
SET badReactionPoint = badReactionPoint+1 
WHERE id = #{id};
